/* minimal browser feature detection used by template; replace with full script if needed */
var browser = {
  name: (function(){
    var ua = navigator.userAgent;
    if(/MSIE|Trident/.test(ua)) return 'ie';
    return '';
  })(),
  mobile: (/Mobi|Android/i.test(navigator.userAgent)),
  canUse: function(feature){
    var div = document.createElement('div');
    return feature in div.style || feature === 'object-fit' && ('objectFit' in div.style);
  }
};
